import React, { useEffect, useState } from "react";
import Header from "../layouts/Header";
import axios from "axios";

const CartPage = ({ cartCount }) => {
    const [cartData, setCartData] = useState([]);

    useEffect(() => {
        fetchCartData();
    }, []);

    const fetchCartData = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/cart');
            if (response.status === 200) {
                setCartData(response.data.data);
            } else {
                console.error('Failed to fetch cart data');
            }
        } catch (error) {
            console.error('Error fetching cart data:', error);
        }
    };


    return (
        <>
            <Header
                cartCount={cartCount} />
            <main className="p-8">
                <h1 className="text-3xl mt-4 mb-3">My Cart</h1>

                {cartData.map((product, index) => (
                    <div key={index} className="my-8 bg-white border-[1px] border-slate-300 rounded shadow-lg p-4">
                        <div className="flex">
                            <img src={product.image} alt="Cart Product" className="w-28 h-28" />
                            <div className="ms-4">
                                <h1 className="text-lg">{product.name}</h1>
                                <div className="text-sky-800 font-semibold">Rp {product.price.toLocaleString()}</div>
                                <div>Total Qty: <span>{product.totalQty}</span> pcs</div>
                            </div>
                        </div>
                    </div>
                ))}
            </main>
        </>
    )
};

export default CartPage;
