import React, { useEffect, useState } from "react";
import Header from "../layouts/Header";
import FormProduct from "../components/FormProduct";

const ProductPage = () => {
    const [products, setProducts] = useState([]);

    // get product data
    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/products');
            const data = await response.json();
            setProducts(data.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    // input
    const [productName, setProductName] = useState('');
    const [productImage, setProductImage] = useState('');
    const [productPrice, setProductPrice] = useState('');
    // alert
    const [alert, setAlert] = useState({ message: '', type: '' });

    // handler input
    const handleProductNameChange = (event) => {
        setProductName(event.target.value);
    }

    const handleProductImageChange = (event) => {
        setProductImage(event.target.value);
    }

    const handleProductPriceChange = (event) => {
        setProductPrice(event.target.value);
    }
    // end handler input

    // handler submit
    const handleSubmit = async (event) => {
        event.preventDefault();

        try{
            const response = await fetch('http://localhost:5000/api/products', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    productName,
                    productImage,
                    productPrice: parseFloat(productPrice),
                }),
            });

            if(!response.ok){
                throw new Error('Failed to add product');
            }

            const data = await response.json();
            console.log('Product was added', data);
            setAlert({ message: 'Product added successfully', type: 'success' });

            setProductName('');
            setProductImage('');
            setProductPrice('');
        }catch(error){
            console.error('Error adding product:', error);
            setAlert({ message: error.message || 'Failed to add product', type: 'error' });
        }
    }

    return (

        <>
            <Header />
            <main className="p-8">
                <h1 className="text-3xl mt-4 mb-5">Manage Our Products</h1>

                <div>
                    <table className="border-[1px] border-gray-600 border-collapse w-full">
                        <thead className="bg-gray-600 text-white">
                            <th>Product Name</th>
                            <th>Catalogue</th>
                            <th>Price</th>
                        </thead>
                        <tbody>
                            {products.map((product) => (
                                <tr key={product.id}>
                                    <td>{product.productName}</td>
                                    <td>{product.productImage}</td>
                                    <td>{product.productPrice}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {alert.message && (
                    <div className={`py-2 px-4 mb-4 rounded ${alert.type === 'success' ? 'bg-green-400' : 'bg-red-400'} text-white`}>
                        {alert.message}
                    </div>
                )}
                <form onSubmit={handleSubmit}>
                    <FormProduct
                        type="text"
                        label="Product Name"
                        id="productName"
                        value={productName}
                        onChange={handleProductNameChange}/>

                    <FormProduct
                        type="text"
                        label="Product Image"
                        id="productImage"
                        value={productImage}
                        onChange={handleProductImageChange}/>

                    <FormProduct
                        type="number"
                        label="Product Price"
                        id="productPrice"
                        value={productPrice}
                        onChange={handleProductPriceChange}/>

                    <div className="flex items-center justify-end mt-5">
                        <button type="submit" className="bg-blue-800 text-white py-2 px-3 rounded">
                            Save</button>
                    </div>
                </form>
            </main>
        </>
    )
};

export default ProductPage;
